prms = jetpack.list(f + 'etalonConv').filter(v=>v.substr(0, 4) == 'page')
  .splice(0,200)
  .map(img=> {
  let i1 = f + 'etalonConv/' + img;
  let i2 = f + 'pdfConv/' + img;

  return new Promise(resolve=> {

    let sst = Date.now();
    var img1 = fs.createReadStream(i1).pipe(new PNG()).on('parsed', doneReading),
        img2 = fs.createReadStream(i2).pipe(new PNG()).on('parsed', doneReading),
        filesRead = 0;

    function doneReading() {
      if (++filesRead < 2) return;
      var diff = new PNG({width: img1.width, height: img1.height});

      var d = pixelmatch(img1.data, img2.data, diff.data, img1.width, img1.height, {threshold: 0.1});
      resolve([img, img1.width , img1.height ,d,Date.now() - sst]);
      diff.pack().pipe(fs.createWriteStream(f+'pdiff/'+img));
    }
  })
})

