clickitem = function (key){
  createMin(key).execute(`Knows.run('${key}')`);
	createEditor('editor_'+key, 'editkopen', key)
}


Knows.getDb().find({ key: { $exists: true } }, function (err, docs) {
  //justShowResult(_.zip(_(docs).pluck('key'),_(docs).pluck('source')).join('\n--\n'))
  //console.log(docs) 
  //return
  var list = _(docs).chain()
	.map(function(v,k){
    return `<span class="nav-group-item knows-item"
onclick="clickitem('${v.key}')">
<span class="icon icon-cd"></span>
${v.key}</span>`;})
  .value().join('\n');

  document.getElementById('content')
    .innerHTML = `<style>
.knows-item {margin:2px 0; background: #ddd; padding: 2px 10px 2px 5px;}
.knows-item .icon {color: #009EBD;}
</style>
<nav class="nav-group" 
style="overflow: scroll;">${list}</nav>`

})

