elName = document.querySelector('.justName');
elName.onclick = function(){
  document.querySelector('.header-text').classList.remove('yellow')
  document.querySelector('.header-text').classList.add('waiting')

  remote.app.once('browser-window-focus', function(e1, w1){
    document.querySelector('.header-text').classList.remove('waiting')
	  document.querySelector('.header-text').classList.add('yellow')
    window.w=w1
    window.e=e1
    Knows.run('grabfocus-test')
  });
}

