    body
      background: none
      opacity: .3
    .wxyp
      position: absolute
      background: none
      color: yellowgreen
      border-top: 1px dashed yellowgreen
      border-left: 1px dashed yellowgreen
      border-right: 1px dashed yellowgreen
      padding-left: 5px
      background: none
    .dblrow    
      //background: #607D8B
      font-family: monospace;
      display: inline-block
      line-height: 1em
      position: relative;

      & .sub, & .sup
        display: block;  
        position: relative;
        top: -0.65em;
        font-size: 0.75em;
        line-height: 0;
        margin: 0 2px;

      & .sub
        top: 0.25em;
.dd
  - each w in windows
    - b = w.object.getBounds()
    - cb = w.object.getBounds()
    .wxyp.file(style="top:#{cb.y-20}px; left:#{cb.x}px; width:#{cb.width}px; height:#{}px;")
       span.icon.icon-cd 
       span.icon.icon-text  #{w.name}
       .pull-right 
        span.icon.icon-info.icon-text

        .dblrow  
          .sup x#{b.x}
          .sub y#{b.y}
        .dblrow 
          .sup w#{b.width}
          .sub h#{b.height}
.aa v1.0.2
`);

function render(){
  txt = tpl(windowManager)
  setContent(txt)
}
render();
window.ri=window.ri||0;
if(ri) clearInterval(ri);
ri = setInterval(render, 1000)

q = !(typeof q == 'boolean') || !q
cw.setIgnoreMouseEvents(true)
cw.setAlwaysOnTop(true)
cw.setPosition(0, 0)
cw.setSize(screen.width, screen.height)

inf = function(w){
  var b = w.object.getBounds()
  return `<sup>x${b.x}</sup><sub>y${b.y}</sub>W${b.width}H${b.height}`  
}
