let key = 'zero';
let zerolist = DNO.open('zerolist');
meta.save_zero = ()=>{
  zerolist.save(key, editor.getValue())  
}

meta.edit_zero = ()=>{
  zerolist.findOne({key}).then(v=>editor.setValue(v.source))  
}

meta.list = ()=>{
  just.funcs.DNO_LIST()
}
meta.s_zero = ()=>{
  zerolist.save(key, '"The zero item"')  
}


let htmlsrc = `<span>zerolist.run('zero')</span> <span>: </span>` 
  + `<button class="btn btn-mini btn-default" onclick="meta.edit_zero()">Edit</button>`
  + `<button class="btn btn-mini btn-default" onclick="meta.save_zero()">Save</button>`
  + `<button class="btn btn-mini btn-default" onclick="meta.list()">!LIST!</button>`
;

